function sendAlert(rule) {
    var alertMsg = {
        topic: "rule alert",
        payload: {
            nodeId: observation.nodeId,
            sensorId: observation.sensorId,
            sensorValue: observation.sensorValue,
            observationId: observation.id,
            ruleId: rule.id
        }
    }
    node.send(alertMsg)    
}

function timeoutAlert(rule) {
    delete heartbeatTimers[rule.id];
    sendAlert(rule);
}

if (observation.sensorId === 0) {
    // sensor 0 is the heartbeat, setup timeout(s)
    var heartbeatTimers = context.get("heartbeatTimers") || {};
    for (var len = rules.length, i = 0; i < len; i++) {
        // stop running timer
        var timer = heartbeatTimers[rules[i].id];
        // reset heartbeat timeout timer
        if (timer) {
            clearTimeout(timer);
        }
        var timeoutSeconds = Number(rules[i].alarm_trigger);
        if (!isNaN(timeoutSeconds)) {
            timer = setTimeout(timeoutAlert, timeoutSeconds * 1000, rules[i]);
            heartbeatTimers[rules[i].id] = timer;            
        }
    }
    context.set("heartbeatTimers", heartbeatTimers);
} else if (!observation.sensorError) {
    // evaluate alarm_trigger(s)
    for (var len = rules.length, i = 0; i < len; i++) {
        try {
            var evalContext = {
                X: value,
                x: value
            };
            var result = safeEval(rules[i].alarm_trigger, evalContext);
            // node.warn("rule evaluation for rule " + rules[i].id +
            //     " on state '" + rules[i].alarm_trigger + "', with x = " + value + " returns " + result);
            if (result) {
                sendAlert(rules[i]);
            }
        } catch (err) {
            // todo: log rule evaluation error
            node.error("rule evaluation error for rule " + rules[i].id +
                " on state '" + rules[i].alarm_trigger + "', with x = " + value);
        }
    }
}

