function convertToTTN(payload) {
  var KPNMsg = payload;
  var TTNMsg = {
    payload: new Buffer(KPNMsg.payload_hex, "hex").toString("base64"),
    port: KPNMsg.FPort,
    counter: 0, // todo: determine counter for KPN lora msg, if not available: redesign counter code
    dev_eui: KPNMsg.DevEUI,
    metadata: [
        {
          server_time: new Date().toISOString(),
          longitude: KPNMsg.LrrLON,      
          lattitude: KPNMsg.LrrLAT
          // other metadata fields ignored for now
        }
    ]
  }
  return TTNMsg;
}

